PlyViewDist = 2PlyViewAng = Angle( 70 , 45 , 0 )PlyViewOffset = Vector( -52 , -52 , 210 )function GM:ShouldDrawLocalPlayer() return true endhook.Add("ShouldDrawLocalPlayer", "ShouldDrawLocalPlayer", function(ply) return true end)ViewFromPos = Vector( 0 , 0 , 0 )function ThirdPersonView(ply, pos, angles, fov)	if not IsValid( LocalPlayer().TankEnt ) then		local view = {}	view.origin = pos	view.origin.z = 600	view.angles = Angle( 80 , 0 , 0 )	view.fov = 100	return view	end		local view = {}	local WallCheckTrace = {}	local SpecPos = LocalPlayer().TankEnt:GetPos()	WallCheckTrace.start = SpecPos + Vector( 0 , 0 , 50 )	ViewFromPos = SpecPos + ( ( PlyViewOffset ) * PlyViewDist )	WallCheckTrace.endpos = ViewFromPos	WallCheckTrace.filter = function( ent ) if not ( ent:GetClass() == "tanktop" or  ent:GetClass() == "tankhover" or  ent:GetClass() == "prop_physics" or  ent:GetClass() == "shell_basic" ) then return true end end	TPVT = util.TraceLine(WallCheckTrace)	view.origin = TPVT.HitPos	view.angles = PlyViewAng	view.fov = fov	return viewendhook.Add("CalcView", "ThirdPersonView", ThirdPersonView) hook.Add("ShouldDrawLocalPlayer", "MyHax ShouldDrawLocalPlayer", function(ply)        return falseend)